	$jsonReglamento=json_decode($reglamento,true);
	$sanciones='';
	$lectura='';
	$unidad_cuenta=75.49;
	if(isset($articulo)&&isset($sancion))
	{
		$articulo_index=$articulo-1;
		$articulo=$jsonReglamento[$articulo_index]['num_art'];

		if(strcmp($sancion,'multa')===0)
		{
			$aplica=$jsonReglamento[$articulo_index]['sanciones'][1];
			if($aplica==1)
			{
				$num_sanciones=count($jsonReglamento[$articulo_index]['tabla'][0]['filas']);
				$filas=count($jsonReglamento[$articulo_index]['tabla'][0]['filas'][0]);
				for($i=0;$i<$num_sanciones-1;$i++)
				{
					$multa_min=$jsonReglamento[$articulo_index]['multas'][$i][0];
					$multa_max=$jsonReglamento[$articulo_index]['multas'][$i][1];
					$multa_min=$multa_min*$unidad_cuenta;
					$multa_max=$multa_max*$unidad_cuenta;

					if($filas>2)
					{
						$desc_fila=$jsonReglamento[$articulo_index]['tabla'][0]['filas'][0][0];
						$fracciones=$jsonReglamento[$articulo_index]['tabla'][0]['filas'][$i+1][0];
						$sanciones=$sanciones.''.$desc_fila.':'.$fracciones.'</br>$'.$multa_min.' a $'.$multa_max.'</br>';
					}
					else
					{
						$sanciones=$sanciones.'$'.$multa_min.' a $'.$multa_max.'</br>';
					}
				}
			}
			else
			{
				$sanciones='No aplica';
			}
			$sanciones=$sanciones;
		}
		elseif (strcmp($sancion,'puntos_licencia')===0) {
			$aplica=$jsonReglamento[$articulo_index]['sanciones'][2];
			if($aplica==1)
			{
				$num_sanciones=count($jsonReglamento[$articulo_index]['tabla'][0]['filas']);
				$filas=count($jsonReglamento[$articulo_index]['tabla'][0]['filas'][0]);
				for($i=0;$i<$num_sanciones-1;$i++)
				{
					if($filas>2)
					{
						$desc_fila=$jsonReglamento[$articulo_index]['tabla'][0]['filas'][0][0];
						$fracciones=$jsonReglamento[$articulo_index]['tabla'][0]['filas'][$i+1][0];
						$multa=$jsonReglamento[$articulo_index]['tabla'][0]['filas'][$i+1][2];
						$sanciones=$sanciones.''.$desc_fila.':'.$fracciones.'</br>'.$multa.'</br>';
					}
					else
					{
						$multa=$jsonReglamento[$articulo_index]['tabla'][0]['filas'][$i+1][1];
						$sanciones=$sanciones.''.$multa.'</br>';
					}	
				}
			}
			else
			{
				$sanciones='No aplica';
			}
			$sanciones=$sanciones;
		}
		elseif ((strcmp($sancion,'corralon')===0)) {
			$aplica=$jsonReglamento[$articulo_index]['sanciones'][3];
			if($aplica==1)
			{
				$sanciones=$sanciones.'El vehículo pude ser remitido al depósito por medio de una unidad de remolque de la Secretaría de Seguridad Publica</br>';
					
			}
			else
			{
				$sanciones='No aplica';
			}
			$sanciones=$sanciones;
		}
		return $sanciones;
	}
	elseif(isset($articulo)&&isset($textoSelec))
	{
		$articulo_index=$articulo-1;
		$articulo=$jsonReglamento[$articulo_index]['num_art'];

		if(strcmp($textoSelec,'resumen_art')===0)
		{
			$resumen=$jsonReglamento[$articulo_index]['res_art'];
			$lectura=$resumen;

		}
		elseif (strcmp($textoSelec,'art_completo')===0) {
			$parrafos_text=count($jsonReglamento[$articulo_index]['text']);

			for($i=0;$i<$parrafos_text;$i++)
			{
				$texto_art=$jsonReglamento[$articulo_index]['text'][$i];
				$lectura=$lectura.''.$texto_art;
			}
			$lectura=$lectura;
		}
		return $lectura;
